insert into antranet70
SELECT antranetsinan.NU_NOTIFIC, antranetsinan.NM_PACIENT ,antranetsinan.DT_NOTIFIC,CONCAT(RIGHT(antranetsinan.SEM_NOT,2),'SE') AS `SEM_NOT`,antranetsinan.ID_UNIDADE, antranetsinan.DT_SIN_PRI,
   CONCAT(RIGHT(antranetsinan.SEM_PRI,2),'SE') AS `SEM_PRI`, antranetsinan.DT_NASC, CONCAT('0',RIGHT(antranetsinan.NU_IDADE_N,2),'A') AS `NU_IDADE_N`,	antranetsinan.CS_SEXO,
   antranetsinan.ID_CNS_SUS, antranetsinan.ID_DISTRIT, antranetsinan.ID_BAIRRO, antranetsinan.NM_LOGRADO, antranetsinan.NU_NUMERO, antranetsinan.NM_COMPLEM,
   CONCAT(LEFT(antranetsinan.NU_CEP,5),'-',RIGHT(antranetsinan.NU_CEP,3)) AS `NU_CEP`, antranetsinan.NU_DDD_TEL, antranetsinan.NU_TELEFON,antranetsinan.ANIMAL, antranetsinan.CONDIC_ANI,
   antranetsinan.OBSERVACAO, antranetsinan.FIM_ANIMAL, antranetsinan.DT_ENCERRA, antranetsinan.OBS
   FROM antranetsinan WHERE ID_DISTRIT='70';

insert into antranet
