    antranet70.`ID_CNS_SUS`,antranet70.`ID_DISTRIT`,antranet70.`ID_DISTRIT`,antranet70.`NM_LOGRADO`,antranet70.`NU_NUMERO`,antranet70.`NM_COMPLEM`,antranet70.`NU_CEP`,antranet70.`NU_DDD_TEL`,antranet70.`NU_TELEFON`,antranet70.`ANIMAL`,
    antranet70.`CONDIC_ANI`,antranet70.`OBSERVACAO`,antranet70.`FIM_ANIMAL`,antranet70.`DT_ENCERRA`,antranet70.`OBS`,ruas.ubs
    FROM antranet70 LEFT JOIN ruas ON antranet70.NU_CEP = ruas.cep;

