SELECT sv2_covid_19.id, "0000000" AS sinan, esus.[Data da Notificação] AS datanot, "COVID-19" AS agravo, UCase([Nome Completo]) AS nome, Int(DateDiff("d",[Data de Nascimento],Date())/365.25) & "A" AS idade, IIf([esus.Sexo]="Feminino","F","M") AS sexo, Date() AS dataentrada, (SELECT SEMEPI FROM ruas_jacana_cep_esus WHERE DASE = Date()) AS se, esus.[Data do início dos sintomas] AS data1sint, "BANCO E-ESUS" AS localate, esus.[Telefone Celular] AS tel, esus.[Número (ou SN para Sem Número)] AS num, esus.Complemento AS [comp], ruas_jacana_cep_esus.DA AS da, Left([ruas_jacana_cep_esus.CEP],2)+Mid([ruas_jacana_cep_esus.CEP],4) AS cep, ruas_jacana_cep_esus.Log AS log, ruas_jacana_cep_esus.ENDEREÇO AS rua, ruas_jacana_cep_esus.BAIRRO AS bairro, ruas_jacana_cep_esus.UBS AS localvd, "UVIS JACANA" AS suvis, "SAO PAULO" AS cidade, ruas_jacana_cep_esus.ID AS idrua, "" AS dataobito, "2020-06-19 13:43:17" AS criado, ruas_jacana_cep_esus.LAT AS latsv2, ruas_jacana_cep_esus.LONG AS longsv2, "sisdamweb D788796" AS usuariocad, "" AS alterado, "" AS usuarioalt, sv2_covid_19.nome AS ocorrencia, "suvis" AS tipo
FROM (ruas_jacana_cep_esus INNER JOIN esus ON ruas_jacana_cep_esus.ENDEREÇO = esus.Logradouro) LEFT JOIN sv2_covid_19 ON esus.[Nome Completo] = sv2_covid_19.nome
WHERE (((sv2_covid_19.nome) Is Null) AND ((ruas_jacana_cep_esus.CEP) In (SELECT[CEP]FROM [ruas_jacana_cep_esus] As Tmp GROUP BY [CEP] HAVING Count(*)=1)) AND ((esus.[Evolução Caso])<>"Cancelado" Or (esus.[Evolução Caso])="" Or (esus.[Evolução Caso]) Is Null));